<template>
    <span v-if="isVisible" class="kuc-input-checkbox-item">
        <input
            type="checkbox"
            v-model="checked"
            :id="id"
            :name="name"
            :disabled="isDisabled"
        />
        <label :htmlFor="id" @click="simulateChange"
            >{{ label }}
        </label>
    </span>
</template>
<script lang="ts">
import Vue from 'vue';
export default Vue.extend({
    model: {
        prop: 'checked',
        event: 'change',
    },
    props: {
        id: {
            type: String,
            required: true,
        },
        name: {
            type: String,
            required: true,
        },
        checked: {
            type: Boolean,
            default: false,
        },
        label: {
            type: String,
            required: true,
        },
        isDisabled: {
            type: Boolean,
            default: false,
        },
        isVisible: {
            type: Boolean,
            default: true,
        },
    },
    methods: {
        simulateChange() {
            if (this.isDisabled) {
                return;
            }
            this.$emit('change', !this.checked);
        },
    },
});
</script>

<style scoped>
.kuc-input-checkbox-item {
    display: block;
    margin-right: 16px;
    margin-bottom: 8px;
    padding-left: 1px;
    max-width: 98%;
}
.kuc-input-checkbox-item:hover + label {
    color: #666;
}
.kuc-input-checkbox-item input[type='checkbox'] {
    display: none;
    cursor: pointer;
}
.kuc-input-checkbox-item input[type='checkbox'] + label {
    position: relative;
    display: inline-block;
    margin-left: 32px;
    vertical-align: middle;
    font-size: 14px;
    cursor: pointer;
}
.kuc-input-checkbox-item
    input[type='checkbox'][disabled]
    + label {
    color: #bababa;
    cursor: not-allowed;
}
.kuc-input-checkbox-item
    input[type='checkbox']
    + label:before {
    position: absolute;
    top: 50%;
    left: -30px;
    display: inline-block;
    box-sizing: border-box;
    margin-top: -11px;
    width: 21px;
    height: 21px;
    background: #fff;
    background: url(data:image/png;base64,)
        no-repeat center center #fff;
    content: '';
}
.kuc-input-checkbox-item
    input[type='checkbox']:checked
    + label:after {
    position: absolute;
    top: 50%;
    left: -30px;
    box-sizing: border-box;
    margin-top: -11px;
    width: 21px;
    height: 21px;
    background: url(data:image/png;base64,)
        no-repeat center center #fff;
    content: '';
}
.kuc-input-checkbox-item
    input[type='checkbox'][disabled]:checked
    + label:after {
    background-image: url(data:image/png;base64,);
}
</style>
